import Layout from "./Layout/Layout";
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import EmployeeCard from "../component/EmployeeCard";
import Container from "react-bootstrap/esm/Container";
import { useEffect, useState } from "react";
import data from './Data.json';
import configData from "../configuration/local.json";


function Employees () {

    const [employees, setEmployees] = useState(data.employees_list);
    

    useEffect(() => {
        fetch(configData.SERVER_URL + "/api/employee/all",{
            method: "GET"
        })
        .then((response) => response.json())
        .then((data) => {
            console.log(data);
            setEmployees(data);
         })
         .catch((err) => {
            console.log(err.message);
         });
    }, [])


    return (
        <Layout className="pt-5" style={{ backgroundColor: "ghostwhite" }}>
            <Container>
            {/* <Row>
            <Col md={8}>
                <InputGroup size="sm" className="mb-3">
                    <InputGroup.Text id="inputGroup-sizing-sm">Search</InputGroup.Text>
                        <Form.Control
                        aria-label="Small"
                        aria-describedby="inputGroup-sizing-sm"
                        />
                </InputGroup>
                </Col>
                <Col md={4}>
                    <div style={{ textAlign : "right" }}>Showing 10 out of 10</div>
                </Col>
                
            </Row> */}
            
            <Row 
            style={{
                mineight: "250px",
                borderRadius: "20%",
                marginTop: "2%"
            }}
            >
                {employees.map((emp, id) => {
                   return <Col key={id} md={3}><EmployeeCard data={emp} /></Col>
                })}
            </Row>
            </Container>
        </Layout>
    );
}

export default Employees;
