import Card from 'react-bootstrap/Card';
import Tooltip from 'react-bootstrap/Tooltip';
import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import "./EmployeeCard.css";


const shades = ["#f5f5dc","#efefdf", "#ffffd2", "#effbff", "#e5ffd4", "#fff3fc", "#efe4ff", "#efefef", "#fff2cc", "#d7ebff"];


function EmployeeCard({ data }) {

  return (
    <Card className='employeeCard'>
      <Card.Body className='text-center'>
        <Card.Title className='employeeDp' style={{ backgroundColor : shades[Math.floor((Math.random() * 10))] }}><div>{data.emp_name.slice(0, 2).toUpperCase()}</div></Card.Title>
        <Card.Title> 
          <OverlayTrigger placement="top" overlay={<Tooltip style={{ overflow : "none" }} interactive="true" id={data.emp_id}>{data.emp_name}</Tooltip>}>
              <div className='employeeName mt-2'>{data.emp_name}</div>
          </OverlayTrigger>
          <div className='employeeId'>({data.emp_id})</div></Card.Title>
        <Card.Text>
          {data.emp_prj}
        </Card.Text>
      </Card.Body>
    </Card>
  );
}

export default EmployeeCard;
